INSERT INTO `admin_notification_templates` VALUES ('applicantActivation','Applicant account activation','Ενεργοποίηση λογαριασμού υποψηφίου','<div style=\"text-align: center\">\r\n    <img style=\"background-color: #2c3e50\" src=\"https://www.grbc.gr/joos/assets/img/logo.png\"><br>\r\n    <br>\r\n    Thank you for your interest<br>\r\n    to study at the Greek Bible College.<br>\r\n    <br>\r\n    To activate your applicant account,<br>\r\n    click <b>Activate</b> and create an account password:<br>\r\n    <br>\r\n    <a href=\"{{appUrl}}/auth/reset?token={{authenticationToken}}\" style=\"text-decoration: none; border-color: #15987e; color: #fff; background-color: #16a085; display: inline-block; padding: 6px 12px; font-size: 14px; line-height: 1.42857143; text-align: center; white-space: nowrap; touch-action: manipulation; cursor: pointer; user-select: none; border: 1px solid transparent; border-radius: 4px;\">Activate</a><br>\r\n    <br>\r\n    If you have questions feel free to <a href=\"https://www.grbc.gr/en/contact/\">contact us</a>.\r\n</div>','<div style=\"text-align: center\">\r\n    <img style=\"background-color: #2c3e50\" src=\"https://www.grbc.gr/joos/assets/img/logo.png\"><br>\r\n    <br>\r\n    Ευχαριστούμε για το ενδιαφέρον σας<br>\r\n    να φοιτήσετε στο Ελληνικό Βιβλικό Κολέγιο.<br>\r\n    <br>\r\n    Για να ενεργοποιήσετε τον λογαριασμό εγγραφής σας,<br>\r\n    επιλέξτε <b>Ενεργοποίηση</b> και ορίστε έναν κωδικό πρόσβασης:<br>\r\n    <br>\r\n    <a href=\"{{appUrl}}/auth/reset?token={{authenticationToken}}\" style=\"text-decoration: none; border-color: #15987e; color: #fff; background-color: #16a085; display: inline-block; padding: 6px 12px; font-size: 14px; line-height: 1.42857143; text-align: center; white-space: nowrap; touch-action: manipulation; cursor: pointer; user-select: none; border: 1px solid transparent; border-radius: 4px;\">Ενεργοποίηση</a><br>\r\n    <br>\r\n    Εάν έχετε ερωτήσεις, επικοινωνήστε μαζί μας στο 210-6038-946\r\n</div>','/auth/reset?token={{authenticationToken}}'),('admissionsΝewApplicant','New applicant registration','Εγγραφή νέου υποψηφίου','<div style=\"text-align: center\">\r\n    <img style=\"background-color: #2c3e50\" src=\"https://www.grbc.gr/joos/assets/img/logo.png\"><br>\r\n    <br>\r\n    New Applicant Registration<br>\r\n    <b>{{lastName}}, {{firstName}}</b><br>\r\n    <br>\r\n    In order to review the Active Registration Applications, click on <b>Applications</b><br>\r\n    <br>\r\n    <a href=\"{{appUrl}}/cp/admission/applications/{{applicantId}}\" style=\"text-decoration: none; border-color: #15987e; color: #fff; background-color: #16a085; display: inline-block; padding: 6px 12px; font-size: 14px; line-height: 1.42857143; text-align: center; white-space: nowrap; touch-action: manipulation; cursor: pointer; user-select: none; border: 1px solid transparent; border-radius: 4px;\">Applications</a>\r\n</div>','<div style=\"text-align: center\">\r\n    <img style=\"background-color: #2c3e50\" src=\"https://www.grbc.gr/joos/assets/img/logo.png\"><br>\r\n    <br>\r\n    Ενημέρωση εγγραφής νέου υποψηφίου<br>\r\n    <b>{{lastName}}, {{firstName}}</b><br>\r\n    <br>\r\n    Για να δείτε τις Τρέχουσες Αιτήσεις Εγγραφής, επιλέξτε <b>Αιτήσεις</b><br>\r\n    <br>\r\n    <a href=\"{{appUrl}}/cp/admission/applications/{{applicantId}}\" style=\"text-decoration: none; border-color: #15987e; color: #fff; background-color: #16a085; display: inline-block; padding: 6px 12px; font-size: 14px; line-height: 1.42857143; text-align: center; white-space: nowrap; touch-action: manipulation; cursor: pointer; user-select: none; border: 1px solid transparent; border-radius: 4px;\">Αιτήσεις</a>\r\n</div>','/cp/admission/applications/{{applicantId}}'),('userPasswordReset','User password reset','Αλλαγή κωδικού πρόσβασης χρήστη','<div style=\"text-align: center\">\r\n    <img style=\"background-color: #2c3e50\" src=\"https://www.grbc.gr/joos/assets/img/logo.png\"><br>\r\n    <br>\r\n    We received a